mapper.update:
UPDATE board_master_t
SET bm_step = bm_step + 1
WHERE bm_group = #{bm_group}
AND bm_step > #{bm_step}

UPDATE board_master_t
SET bm_title = #{bm_title}

    ,bm_writer = #{bm_writer}


    ,bm_content = #{bm_content}


    ,bm_pw = #{bm_pw}

		,bm_reg = TO_CHAR(sysdate, 'YYYY-MM-DD')
WHERE bm_no = #{bm_no}
